import React from 'react';
//import ChooseActivity from './ChooseActivity';
import ShowCurrentActivity from './ShowCurrentActivity';
import ShowSimilarUsers from './ShowSimilarUsers';
import { Socket } from 'phoenix-socket';

class User extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      channel: null,
      socket: null,
      presenceState:{},
      current: "Nothing"
    };

    // This binding is necessary to make `this` work in the callback
    this.joinRoom = this.joinRoom.bind(this);
    this.sendMessage = this.sendMessage.bind(this);
  }

  sendMessage() {
    const { channel } = this.state;
    channel.push('shout', {name: this.props.usernumber + " is " + this.state.current})
  }

  joinRoom(data) {
    const { socket } = this.state;
    this.setState({data})
    this.setState({current: data})
    const channel = socket.channel(`water_cooler:${data}`);
    this.setState({channel: channel})
    socket.connect();
    channel.join()
    .receive("ok", resp => {
      console.log("Joined successfully" + this.props.usernumber, resp)
    })
    .receive("error", resp => { console.log("Unable to join", this.props.usernumber) })

    channel.on(`room:${data}:new_message`, payload => {
        if (payload.name !== this.props.usernumber + " is " + this.state.current){
          console.log(payload)
          console.log("user "+ this.props.usernumber + " got the message")
        }

    })

    channel.on('presence_state', presenceState => this.setState({presenceState}))


    //channel.push('users', {})
  }

  componentDidMount() {
    const socket = new Socket("ws://127.0.0.1:4000/socket", {
    params: {user_id: `user #${this.props.usernumber}`}
    });
    this.setState({socket})
  }

  render() {

    return (
      <div className="User">
        <ShowCurrentActivity current={this.state.current}/>
        <ChooseActivity join={this.joinRoom} message={this.sendMessage}/>

        <ShowSimilarUsers users={this.state.presenceState}/>
      </div>
    );
  }
}

export default User;


