import React from "react";
import {
  withGoogleMap,
  GoogleMap,
  Marker,
  withScriptjs
} from "react-google-maps";
import { Socket } from 'phoenix-socket';


const Markers = ({ places }) => {
  return places.forEach((value, key) => {
    return (
      <Marker key={key} position={{ lat: value.latitude, lng: value.longitude }} />
    );
  });
};

const Map = ({ places, zoom, center }) => {
  return (
    <GoogleMap defaultZoom={zoom} defaultCenter={center}>
      <Markers places={places} />
    </GoogleMap>
  );
};

// const getRandomInRange = (from, to, fixed) => {
//   return (Math.random() * (to - from) + from).toFixed(fixed) * 1;
// };

class MapWithMarker extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      moreplaces: new Map(),
      places: this.props.places
    }; //initialize initial state from props
  }

  addPlace() {
    const newPlace = {
      id: this.state.places.length + 1,
      lat: getRandomInRange(-30.0, -35.0, 3),
      lng: getRandomInRange(110.0, 150.0, 3)
    };
    this.setState(prevState => ({
      places: [...prevState.places, newPlace]
    }));

    /*if(this.state.places.length > 10) {
      clearInterval(this.intervalId)
    }*/
  }

  componentDidMount() {
    //this.intervalId = setInterval(this.addPlace.bind(this), 1000);
    var map = new Map();
    const socket = new Socket("ws://127.0.0.1:4000/socket", {
    params: {user_id: "gpsmap"}
    });
    const channel = socket.channel("water_cooler:gps");
    socket.connect();
    channel.join()
    .receive("ok", resp => {
      console.log("Joined successfully ", resp)
    })
    .receive("error", resp => { console.log("Unable to join") })

    channel.on("gps", payload => {
      map.set(payload.user,payload.data)
      console.log(map)
      this.setState({moreplaces: map})

    })
  }

  componentWillUnmount() {
    clearInterval(this.intervalId);
  }

  render() {
    return (
      <Map
        center={this.props.center}
        zoom={this.props.zoom}
        places={this.state.moreplaces}
      />
    );
  }
}

export default withScriptjs(withGoogleMap(MapWithMarker));
