import React from 'react';
//import ChooseActivity from './ChooseActivity';
import ShowCurrentActivity from './ShowCurrentActivity';
import ShowSimilarUsers from './ShowSimilarUsers';
import { Socket } from 'phoenix-socket';

const socket = new Socket("ws://127.0.0.1:4000/socket", {
params: {user_id: usernumber}
});


const channel = socket.channel(`water_cooler:${data}`);


function User({usernumber}) {





  function sendMessage(data) {
    channel.push('shout', {name: usernumber + " is " + data})
  }

  function joinRoom(data) {



    socket.connect();
    channel.join()
    .receive("ok", resp => { console.log("Joined successfully" + usernumber, resp) })
    .receive("error", resp => { console.log("Unable to join", usernumber) })

    channel.on('shout', payload => {
        if (payload.name !== usernumber + " is here "){
          console.log(payload)
          console.log("user "+ usernumber + " got the message")
        }

    })

    channel.on('presence_state', presenceState => console.log(presenceState))
  }




  return (
    <div className="User">
      <ShowCurrentActivity/>
      <div className="ButtonList">
        Choose next activity?<br/>
        <button onClick={() => joinRoom("washing")} >
          Wash
        </button>
        <button onClick={() => joinRoom("dishes")} >
          Dishes
        </button>
        <button onClick={() => joinRoom("studying")}>
          Study
        </button>
        <button onClick={() => sendMessage("here")}>
          Send Message
        </button>
        <hr/>


      </div>
      <ShowSimilarUsers/>

    </div>
  );
}

export default User;
