import React from 'react';
//import ChooseActivity from './ChooseActivity';
import ShowCurrentActivity from './ShowCurrentActivity';
import ShowSimilarUsers from './ShowSimilarUsers';
import { Socket, Presence } from 'phoenix-socket';

class User extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      room: "",
      presence: "",
      channel: null,
      socket: null
    };

    // This binding is necessary to make `this` work in the callback
    this.joinRoom = this.joinRoom.bind(this);
    this.sendMessage = this.sendMessage.bind(this);
  }

  sendMessage() {
    const { channel } = this.state;
    channel.push('shout', {name: this.props.usernumber + " is " + this.state.data})
  }

  joinRoom(data) {
    const { socket } = this.state;
    this.setState({data})
    const channel = socket.channel(`water_cooler:${data}`);
    this.setState({channel: channel})
    socket.connect();
    channel.join()
    .receive("ok", resp => {
      console.log("Joined successfully" + this.props.usernumber, resp)
      this.setState({room: data})
    })
    .receive("error", resp => { console.log("Unable to join", this.props.usernumber) })

    channel.on(`room:${data}:new_message`, payload => {
        if (payload.name !== this.props.usernumber + " is here "){
          console.log(payload)
          console.log("user "+ this.props.usernumber + " got the message")
        }

    })

    channel.on('presence_state', presenceState => console.log(presenceState))


    //channel.push('users', {})
  }

  componentDidMount() {
    const socket = new Socket("ws://127.0.0.1:4000/socket", {
    params: {user_id: this.props.usernumber}
    });
    this.setState({socket})
  }

  render() {
    console.log(this.state, " this.state")
    return (
      <div className="User">
        <ShowCurrentActivity/>
        <div className="ButtonList">
          Choose next activity?<br/>
          <button onClick={()=>this.joinRoom("washing")} >
            Wash
          </button>
          <button onClick={()=>this.joinRoom("dishes")} >
            Dishes
          </button>
          <button onClick={()=>this.joinRoom("studying")}>
            Study
          </button>
          <button onClick={this.sendMessage}>
            Send Message
          </button>
          <hr/>


        </div>
        <ShowSimilarUsers/>

      </div>
    );
  }
}

export default User;
